DROP DATABASE IF EXISTS verse;
CREATE DATABASE verse;
\c verse

-- USERS
CREATE TABLE users(
    id SERIAL PRIMARY KEY,
    name VARCHAR NOT NULL,
    email VARCHAR UNIQUE NOT NULL,
    password_digest VARCHAR NOT NULL,  
    picture VARCHAR
);

-- AUTORS
CREATE TABLE autor(
    id SERIAL PRIMARY KEY,
    autor_name VARCHAR NOT NULL,
    autor_lastname VARCHAR NOT NULL,
    pen_name VARCHAR,
    picture VARCHAR,
    bio VARCHAR,
    born VARCHAR,
    died VARCHAR,
    birthplace VARCHAR
);

-- BOOKS
CREATE TABLE book(
    id SERIAL PRIMARY KEY,
    cover VARCHAR,
    title VARCHAR NOT NULL,
    serie VARCHAR,
    synopsis VARCHAR,
    published_date VARCHAR,
    pages VARCHAR,
    autor_id INT REFERENCES autor(id) ON DELETE SET NULL,
    genre_id INT REFERENCES autor(id) ON DELETE SET NULL,
    type_id INT REFERENCES type(id) ON DELETE SET NULL,
    status VARCHAR
);

-- GENRES
CREATE TABLE genres(
    id SERIAL PRIMARY KEY,
    genre VARCHAR NOT NULL,
);

-- TYPES OF BOOKS 
CREATE TABLE types_book(
    id SERIAL PRIMARY KEY,
    type VARCHAR NOT NULL,
);

-- REVIEWS 
CREATE TABLE reviews(
    id SERIAL PRIMARY KEY,
    reviewer INT REFERENCES users(id) ON DELETE SET NULL,
    content VARCHAR,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    rating VARCHAR,
    book_id INT REFERENCES book(id) ON DELETE SET NULL
);

-- CREATE TABLE reading_process(
--     id SERIAL PRIMARY KEY,
--     finished BOOLEAN,
--     reading BOOLEAN,
--     untouched BOOLEAN,
--     book_id INT REFERENCES book(id) ON DELETE SET NULL,
--     user_id INT REFERENCES users(id) ON DELETE SET NULL 
-- );

-- FAVORITE LIST
CREATE TABLE fav(
    id SERIAL PRIMARY KEY,
    book_id INT REFERENCES book(id) ON DELETE SET NULL,
    user_id INT REFERENCES users(id) ON DELETE SET NULL 
);

-- BOOKSHELF, FOR FUTURE READING
CREATE TABLE bookshelf_user(
    id SERIAL PRIMARY KEY,
    book_id INT REFERENCES book(id) ON DELETE SET NULL,
    user_id INT REFERENCES users(id) ON DELETE SET NULL 
);

